    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        String jenis23081019 = cmbJenisAnak23081019.getSelectedItem().toString();
        int biaya23081019;
        switch (jenis23081019) {
            case ">= 7 Tahun":
                biaya23081019 = 75000;
                break;
            case "< 7 Tahun":
                biaya23081019 = 35000;
                break;
            case "Tidak Membawa Anak":
                biaya23081019 = 0;
                break;
            default:
                biaya23081019 = 0;
                break;
        }
    txtBiayaAnak23081019.setText(String.valueOf(biaya23081019));
    }//GEN-LAST:event_jButton4ActionPerformed

