    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        int totalTiket23081019 = Integer.parseInt(txtTotalHargaTiket23081019.getText());
        int biayaAnak23081019 = Integer.parseInt(txtBiayaAnak23081019.getText());
        double biayaBarang23081019 = Double.parseDouble(txtBiayaBarang23081019.getText());


        // Hitung total biaya
        double totalBiaya = totalTiket23081019 + biayaAnak23081019 + biayaBarang23081019;


        // Tampilkan ke text field
        txtTotalBiaya23081019.setText(String.valueOf(totalBiaya));
    }//GEN-LAST:event_jButton2ActionPerformed

