    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
    double totalBiaya23081019 = Double.parseDouble(txtTotalBiaya23081019.getText());

    double diskon23081019;
    if (rdbMember23081019.isSelected()) {
        diskon23081019 = totalBiaya23081019 * 0.05;  // Member: diskon 5%
    } else {
        diskon23081019 = 0;  // Non Member
    }

    // Tampilkan diskon
    txtDiskon23081019.setText(String.valueOf(diskon23081019));

    // Hitung total pembayaran
    double totalPembayaran = totalBiaya23081019 - diskon23081019;
    txtTotalPembayaran23081019.setText(String.valueOf(totalPembayaran));
    }//GEN-LAST:event_jButton3ActionPerformed

